/**

* @param {number[]} nums 需要处理的数组
* @return {number} 新数组长度
*/
var removeDuplicates = function(nums) {

 var j = 0;

 for (var i = 1; i < nums.length; i++) {

   // 因为是已经排序，这里要比较相邻两个元素,是否相等
   if (nums[j] != nums[i]) {
     nums[++j] = nums[i]; // 第一次循环，nums[j]为1，nums[i]为2，第一个一定会有，不用管，只管++j；如果相等，则新数组不操作，不等则将原值复制过来
   }

 }

 return j+1;

};


